import React, { useState } from "react";
import axios from "axios";
import "./Weather.css";

export default function Weather() {
  const [ready, SetReady] = useState(false);
  const [weatherData, setWeatherData] = useState({});
  function handleResponse(response) {
    console.log(response.data);
    setReady(true);
  }

  if (ready) {
    let fakeData = {
      temperature: 9,
      city: "New York",
      date: "Saturday, 11:03",
      weather: "Overcast cloud",
      img: "http://openweathermap.org/img/wn/04n@2x.png",
    };
    return (
      <div className="card">
        <form>
          <div className="row">
            <div className="col">
              <div className="temp-today">
                <strong>{fakeData.temperature}</strong>
                <span className="units">
                  <a href="/" className="active">
                    °C
                  </a>{" "}
                  |<a href="/">°F</a>
                </span>
              </div>
              <h1 className="city">
                <strong>{fakeData.city}</strong>
              </h1>
              <div>
                <h2 className="date">{fakeData.date}</h2>
                <h2 className="weather">{fakeData.weather}</h2>
              </div>
            </div>
            <div className="col">
              <img
                src={fakeData.img}
                className="fa-solid fa-sun"
                alt="weatherIcon"
              />
            </div>
          </div>
        </form>
      </div>
    );
  } else {
    let city = "New York";
    let apiKey = "44144f3a5bac41c0c3eb91949dd3a0c9";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);
  }
}
